mapper.insert:
insert into sys_role_permission (id, roleid, permissionid, 
  eisdel, ectime, eutime
  )
values (#{id,jdbcType=BIGINT}, #{roleid,jdbcType=BIGINT}, #{permissionid,jdbcType=BIGINT}, 
  #{eisdel,jdbcType=BIT}, #{ectime,jdbcType=TIMESTAMP}, #{eutime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 

from sys_role_permission
where eisdel=0 and roleid=#{arg0}

mapper.update:
update sys_role_permission
   set eisdel = 1
where eisdel=0 and roleid =#{arg0}

update sys_role_permission
   set eisdel = 1
where eisdel=0 and permissionid =#{arg0}
